class Questions extends React.Component{

constructor(props){
    super(props);

    this.handleOptionChange = this.handleOptionChange.bind(this); 
}

handleOptionChange(e){
    this.setState({
          selectedOption: e.target.value
        });
        console.log(e.target.value)
      }


    render(){
        return(

<div class="preguntas">
        <h1>{this.props.question}</h1>
            <table>
                <tr>
        <td><input type="radio" name = "answer" onChange ={this.handleOptionChange} ></input>{this.props.answer1}</td>
        <td><input type="radio" name = "answer"  ></input>{this.props.answer2}</td>
                </tr>

                <tr>
        <td><input type="radio" name = "answer" ></input>{this.props.answer3}</td>
        <td><input type="radio" name = "answer" ></input>{this.props.answer4}</td>
                </tr>
            </table>
<button>Check</button>
<button >Next Question</button>
</div>

        );


    }

}

export default Questions
