import React from 'react'
import Intro from './Intro'
import Questions from './Questions'

class Main extends React.Component{


constructor(props){
    super(props)

    this.state ={
        question: "Como se llama pablo",
        realanswer:"pedro",
        selectedOption: "",
        answer1 : "pedro",
        answer2 : "juan",
        answer3 : "duarte",
        answer4 : "mella",
        round: 0,
        score: 0
    }
    this.check = this.check.bind(this);
}

check(){
    if(this.state.selectedOption == this.state.realanswer){
        console.log("Correcto")
    }else{
        console.log("Falso")
    }
}

    render(){
        return(
<div className ="container">

<Intro round = {this.state.round} points = {this.state.round}/>
<Questions selectedOption = {this.selectedOption} question = {this.state.question} answer1 = {this.state.answer1} answer2= {this.state.answer2}  answer3 = {this.state.answer3} answer4 = {this.state.answer4} />
<button onClick = {this.check}>Check</button>
<button >Next Question</button>
</div>
        );


    }

}

export default Main
