import React from 'react'

class Questions extends React.Component{


    render(){
        return(

<div class="preguntas">
        <h1>{this.props.question}</h1>
            <table>
                <tr>
        <td><input type="radio" name = "answer" checked={this.props.selectedOption = this.props.answer1} ></input>{this.props.answer1}</td>
        <td><input type="radio" name = "answer" checked={this.props.selectedOption = this.props.answer2} ></input>{this.props.answer2}</td>
                </tr>

                <tr>
        <td><input type="radio" name = "answer" checked={this.props.selectedOption =this.props.answer3} ></input>{this.props.answer3}</td>
        <td><input type="radio" name = "answer" checked={this.props.selectedOption =this.props.answer4} ></input>{this.props.answer4}</td>
                </tr>
            </table>
<button>Check</button>
<button >Next Question</button>
</div>

        );


    }

}

export default Questions
